mapper.update:
    	UPDATE users
    	SET pw=#{pw}, nick_name=#{nickName}, phone1=#{phone1}, phone2=#{phone2}, phone3=#{phone3}, grade=#{grade}, interest=#{interest}, addr_basic=#{addrBasic}, addr_detail=#{addrDetail}, zip_num=#{zipNum}, major=#{major}, profile=#{profile}, scrap=#{scrap}
		WHERE id=#{id}

mapper.delete:
DELETE FROM users
WHERE id=#{id}
AND pw=#{pw}

mapper.select:
SELECT * FROM users
WHERE id = #{id}
AND pw = #{pw}
